INSERT INTO `food` (`id`, `img`, `title`, `title_sign`, `subcontent`, `fullcontent`, `price`, `origin`, `id_cat`) VALUES
(1, '1576561601_Tellme---Pate-gan-thịt-heo-cho-chó-130gr.jpg', 'Tellme - Pate gan thịt heo cho chó 130gr', 'Tellme - Pate gan thit heo cho cho 130gr', 'TELLME Pa tê Gan Thịt Heo bổ sung Canxi chiết xuất từ vỏ trứng và dầu Ô liu được chế biến theo công nghệ Túi hầm chịu nhiệt, khóa chọn dinh dưỡng, không có chất bảo quản giúp các bạn cún yêu bổ sung can xi tự nhiên và dầu ô liu giúp xương chắc khỏe, lông bóng mượt.', '<p><p><strong><a href=\"https://www.petcity.vn/tellme-pate-gan-thit-heo-cho-cho-130gr.html\">Pate gan thịt heo</a><a href=\"https://www.petcity.vn/pate-cho-cho-tellme-thit-heo.html\">&nbsp;cho chó</a></strong></p><p>&nbsp;CÔNG NGHỆ TÚI HẦM CHỊU NHIỆT, GIỮ TRỌN 100% DINH DƯỠNG!</p><p><img src=\"https://www.petcity.vn/media/lib/4124_Pate-cho-1.jpg\" alt=\"\" width=\"450\" style=\"\"></p><p>TELLME Pa tê Gan Thịt Heo bổ sung Canxi chiết xuất từ vỏ trứng và dầu Ô liu được chế biến theo công nghệ Túi hầm chịu nhiệt, khóa chọn dinh dưỡng, không có chất bảo quản giúp các bạn cún yêu bổ sung can xi tự nhiên và dầu ô liu giúp xương chắc khỏe, lông bóng mượt.</p><p>Với thành phần Gan Heo, Gan bò giàu vitamin D, vitamin E, vitamin A kết hợp bổ sung thêm chất xơ từ cà rốt giúp các cún yêu bổ sung các vitamin một cách đơn giản với một thực đơn hợp lý thông qua bữa ăn hàng ngày.</p><p><strong>Giới thiệu về&nbsp;<a href=\"https://www.petcity.vn/brand/tellme-viet-nam\">Tellme</a></strong></p><p>- &nbsp;Được làm 100% Da bò tự nhiên</p><p>‌- Làm hoàn toàn thủ công, không hóa chất và sấy khô ở nhiệt độ thích hợp giúp giữ nguyên dưỡng chất.</p><p>-‌ Giúp răng chắc khỏe và sạch sẽ</p><p>‌- Collagen trong da bò giúp lông cún bóng mượt, khỏe mạnh, hạn chế rụng lông, làm chậm sự lão hóa của các tế bào cơ thể</p></p>', 50, 'Viet Nam', 1),
(2, '1576561890_Pate-cho-chó-con-Pedigree-bò-sốt-130g.jpg', 'Pate cho chó con Pedigree bò sốt 130g', 'Pate cho cho con Pedigree bo sot 130g', 'Pate cho chó con Pedigree bò sốt 130g ( Beef Chunks Flavour)', '<p><h1><a href=\"http://meowwoof.vn/thuc-an-cho-cho.html\">Thức ăn cho chó</a></h1><p>[img src=\"https://meowwoof.vn/pub/media/wysiwyg/SanphamMeowwoof/Pedigree/Pate-Pedigree-goi-cho-cho-con-vi-bo-sot-luc-lac-130g.jpg\" alt=\"Pate cho chó con Pedigree bò sốt\"&gt;</p><p>MÔ TẢ</p><p>Pedigree là dòng sản phẩm quen thuộc của các chủ nhà Việt Nam khi chọn thức ăn cho chó cưng của mình.</p><p><strong>Pate cho chó</strong>&nbsp;với nguồn dinh dưỡng đầy đủ giúp cún mau lớn.</p><p>Pedigree cho ra đời những thực phẩm bổ dưỡng giúp chó nhà bạn phát triển toàn diện và khoẻ mạnh mỗi ngày.</p><p><strong>ĐẶC ĐIỂM VÀ LỢI ÍCH&nbsp;</strong></p><p>Thức ăn cho chó thơm ngon với vị bò sốt&nbsp; phù hợp với tất cả khẩu vị của những chú chó nhà bạn, ngay cả những chú chó kén ăn nhất.&nbsp;</p><p><strong>Thức ăn ướt cho chó</strong>&nbsp;bổ sung các chất dinh dưỡng cần thiết cho chó.</p><p><strong>Thức ăn cho chó</strong>&nbsp;giúp chó khoẻ mạnh và tăng trưởng đều đặn.</p><p>Các thành phần dễ hấp thụ giúp hệ tiêu hoá của chó nhà bạn được bảo vệ chặt chẽ.&nbsp;</p><p><strong>THÀNH PHẦN</strong>&nbsp;</p><p>Độ đạm (tối thiểu) ..................................... 8%<a href=\"https://meowwoof.vn/thuc-an-cho-cho.html\"></a></p><p>Độ béo (tối thiểu) ...................................... 3%</p><p>Chất xơ (tối đa) ......................................... 1 %</p><p>Độ ẩm (tối đa) ........................................... 82%</p><p>Linoleic Acid (tối thiểu) .............................. 0.7%</p><p>Vitamin E (tối thiểu) .................................. 25 IU/KG</p><p><strong>HƯỚNG DẪN SỬ DỤNG</strong></p><p><strong>-&nbsp;</strong>Đậy kín sau khi không sử dụng, tránh sự xâm nhập của chuột bọ.</p><p>- Để xa tầm tay của trẻ em.</p><br></p>', 23, 'Thai Lan', 1),
(3, '1576562045_Pate-Cho-Chó-Con-Smartheart.jpg', 'Pate Cho Chó Con Smartheart', 'Pate Cho Cho Con Smartheart', 'Pate Cho Chó Con Smartheart (130g)', '<p><p>Cung cấp nhiều chất dinh dưỡng cần thiết</p><p>Mùi vị thơm ngon kích thích vị giác thú cưng</p><p>Chống bệnh chán ăn, bỏ ăn</p><p>Hỗ trợ phát triển não và bộ xương</p><h3>MÔ TẢ SẢN PHẨM</h3><p></p><div><div><div><ul><li>Pate Cho Chó Con Smartheart (130g)&nbsp;là sản phẩm dành cho thú cưng của bạn, chứa nhiều chất dinh dưỡng giúp cơ thể chuyển hóa nhanh chất dinh dưỡng đi nuôi cơ thể sẽ là một sản phẩm hữu ích toàn diện, ngoài việc để cho những thú cưng của bạn có bữa ăn no, thì sản phẩm còn có tác dụng bồi bổ, cung cấp các chất dinh dưỡng cho quá trình phát triển.</li><li>Giúp phòng ngừa bệnh tật, bảo đảm sự phát triển khỏe mạnh của những chú chó nhà bạn.</li><li>Ngoài ra, với mùi vị thơm ngon kích thích vị giác giúp thú cưng dễ hấp thu và tiêu thụ sản phẩm một cách nhanh hơn.</li></ul><p></p></div></div></div><br></p>', 18, 'Thai Lan', 1),
(4, '1576562268_Pate-Pedigree-Cho-Chó-Lon-400g.jpg', 'Pate Pedigree Cho Chó Lon 400g', 'Pate Pedigree Cho Cho Lon 400g', 'Pate Pedigree Cho Chó Lon 400g, Thức Ăn Cho Chó', '<p><h2>Mô tả</h2><p><a href=\"https://petviet.vn/chuyen-muc/san-pham-cho-cho/thuc-an-cho-cho/thuc-an-uot-cho-cho\"><strong>Thức Ăn Cho Chó</strong></a>&nbsp;Pate Pedigree cho Chó; với từng miếng thịt mềm trong nước sốt cho hương vị thơm ngon mà những chú chó cực kì yêu thích. Một chế độ dinh dưỡng đầy đủ và cân bằng bao gồm các vitamin, khoáng chất, canxi, protein, chất xơ và omega; cho cơ thể các chú cún có một sức khỏe toàn diện; bảo đảm sự phát triển cả về thể chất lẫn ngoại hình.</p><p><strong>Thành phần</strong></p><p>Thịt gà, gia vị bò và nước sốt</p><p><strong>Thông tin dinh dưỡng</strong></p><p>Các Vitamin C, A, D, D3, B2, B12, B6, E và B1, và bổ sung các khoáng chất canxi, đồng sulfat, kẽm, kali clorua, sắt cho chó.</p><p><strong>Hướng dẫn sử dụng hàng ngày</strong></p><p>Bạn có thể cho cún ăn kèm cơm và bành mì. Là loại thức ăn mềm nên sử dụng được cho chó ở nhiều độ tuổi khác nhau.</p><p>Các bạn cũng nên chú ý hạn sử dụng của sản phẩm in trên bao bì; để đảm bảo không dùng sản phẩm quá hạn.</p><br></p>', 45, 'Thai Lan', 1),
(5, '1576562525_Natural-Core---Thức-ăn-hữu-cơ-cho-chó-con-vị-thịt-cừu-(7kg).jpg', 'Natural Core - Thức ăn hữu cơ cho chó con vị thịt cừu (7kg)', 'Natural Core - Thuc an huu co cho cho con vi thit cuu (7kg)', 'Natural Core - Thức ăn hữu cơ cho chó con vị thịt cừu (7kg)\r\nThức ăn hữu cơ cho chó con Natural Core thịt cừu được chế biến từ các loại thịt tươi và các nguyên liệu được chứng nhận hữu cơ ECOCERT: thịt cừu Úc và thịt nạc gà hữu cơ, khoai lang hữu cơ và ngũ cốc. Với nhiều chất dinh dưỡng tốt cho sức khỏe thú cưng, ECO5a có tác dụng nổi bật trong việc hỗ trợ sự phát triển của chó con.\r\n', '<p><h4><strong><span><a href=\"https://www.petcity.vn/natural-core-thuc-an-huu-co-cho-cho-con-vi-thit-cuu-7kg.html\">Thức ăn hữu cơ cho chó con vị thịt cừu</a></span></strong></h4><p>Trọng lượng : 7kg&nbsp;</p><h4>Đặc điểm nổi bật</h4><p>Thức ăn hữu cơ cho chó con Natural Core thịt cừu được chế biến từ các loại thịt tươi và các nguyên liệu được chứng nhận hữu cơ ECOCERT: thịt cừu Úc và thịt nạc gà hữu cơ, khoai lang hữu cơ và ngũ cốc. Với nhiều chất dinh dưỡng tốt cho sức khỏe thú cưng, ECO5a có tác dụng nổi bật trong việc hỗ trợ sự phát triển của chó con.</p><ul><li>Dành cho chó con dưới 2 tuổi, chó mẹ đang mang thai và cho con bú</li><li>Thành phần: thịt cừu Úc và thịt nạc gà hữu cơ, khoai lang hữu cơ và ngũ cốc</li><li>Không chứa thành phần gây dị ứng cho chó con như bắp, đậu nành, lúa mì</li><li>Tốt cho việc hình thành xương và giúp tăng cường chức năng đường ruột</li><li>Ngăn ngừa tình trạng dị ứng thức ăn ngay từ khi còn nhỏ</li><li>Tăng cường chức năng đường ruột và nâng cao hệ miễn dịch</li><li>Cung cấp protein chất lượng cao cho cún con tăng trưởng và phát triển</li><li>Cho cún con một làn da khỏe mạnh và bộ lông óng mượt</li></ul><p></p><h4>Đặc tính - Công dụng</h4><p>&nbsp;- Thành phần hữu cơ tốt cho sức khỏe, đặc biệt phù hợp với chó con và chó mẹ</p><p>&nbsp;- Ngăn ngừa tình trạng dị ứng thức ăn thường gặp ở cún con</p><p>&nbsp;- Công nghệ đạm thủy phân, tối đa hóa khả năng hấp thu</p><p>&nbsp;- Tăng cường hệ miễn dịch, sức đề kháng, cho hệ tiêu hóa khỏe mạnh</p><p>&nbsp;- Cho cún cưng làn da khỏe mạnh và bộ lông óng mượt</p><p><strong>Khẩu phần ăn</strong></p><p><strong><img src=\"https://www.petcity.vn/media/lib/3493_NCchconthtcu.bmp\" alt=\"\"></strong></p><p><strong>Giới thiệu về&nbsp;<a href=\"https://www.petcity.vn/brand/natural-core\">Natural Core&nbsp;</a></strong></p><p>Natural Core sử dụng các công nghệ chế biến thực phẩm cấp độ của con người cùng với các thành phần hữu cơ để sản xuất thức ăn cho chó và mèo chất lượng cao nhất.&nbsp;</p><br></p>', 145, 'Hoa Kỳ', 2),
(6, '1576562682_Royal-canin-Medium-Puppy-(10kg).jpg', 'Royal canin Medium Puppy (10kg)', 'Royal canin Medium Puppy 10kg', 'Royal canin - Medium Puppy 10kg Là sản phẩm thức ăn khô dành cho giống chó cỡ trung bình từ 2 - 12 tháng (cân nặng tối đa 11 - 25 kg )', '<p><h2><a href=\"https://www.petcity.vn/royal-canin-medium-junior-10kg/p2146.html\">Royal canin - Medium&nbsp;Puppy 10kg</a></h2><p><strong>- Là sản phẩm thức ăn khô dành cho giống chó cỡ trung bình từ 2 - 12 tháng (cân nặng tối đa 11 - 25 kg )</strong></p><p><strong>LỢI ÍCH&nbsp;</strong></p><p>&nbsp;- Sự kết hợp độc đáo của các chất dinh dưỡng để&nbsp;<strong><em>hỗ trợ tiêu hóa</em></strong>&nbsp;với các protein tiêu hóa cao (LIP) và sự cân bằng đường ruột&nbsp;<em><strong>hỗ trợ hệ bài tiết</strong></em></p><p>-&nbsp;Giúp hỗ trợ &nbsp;<em><strong>phát triển xương</strong></em>&nbsp;bền vững ở các giống chó Medium nhờ vào công thức giúp hấp thu hiệu quả Canxi và phốt pho</p><p>- Hỗ trợ&nbsp;<em><strong>nhu cầu năng lượng cao</strong></em>&nbsp;cho sự tăng trưởng mạnh mẽ ở lứa tuổi 2 đến 12 vốn dĩ cần rất nhiều chất dinh dưỡng</p><p>-&nbsp;<strong><em>Tăng cường hệ miễn dịch</em></strong>&nbsp;để ngăn ngừa bệnh tật&nbsp;</p><p><strong>THÀNH PHẦN</strong></p><p><strong>Nguyên Liệu:</strong></p><p>Bột mì, lúa mạch, bột gluten bắp, chất béo gà, ngô, gluten lúa mì, bột củ cải đường khô, lúa mì, hương vị tự nhiên, bột mì gạo, dầu cá, canxi cacbonat, men distillers khô men, natri silic aluminat, Dầu thực vật, kali phốt phát, muối, fructooligosaccharides, L-lysine, men thủy phân, clorua choline, taurine, DL-methionine, vitamin [DL-alpha tocopherol acetate (nguồn vitamin E), L-ascorbyl-2-polyphosphate Vitamin B6, vitamin B6, vitamin B6, vitamin B2, vitamin B2, vitamin B2, vitamin C, vitamin C, vitamin C, Kẽm proteinate, oxit kẽm, sắt sulfate, protein mangan, oxit mangan, đồng sulfat, canxi iodat, proteinate đồng, selenit natri), chiết xuất thảo mộc, bảo quản với tocopherols hỗn hợp và axit xitric.</p><p><strong>Thành Phần Dinh Dưỡng</strong></p><h2>&nbsp;<img src=\"http://cityzoo.vn/img_data/images/thanhphanmedium.png\" alt=\"thuc-an-hat-royal-canin-medium-junior\"></h2><h2><strong>KHẨU PHẦN ĂN&nbsp;</strong></h2><p>&nbsp;<img src=\"http://cityzoo.vn/img_data/images/khauphanmediumjunior(1).png\" alt=\"thuc-an-hat-royal-canin-medium-junior\"></p><p><strong>Nhà Sản Xuất</strong>: Royal Canin</p><p><strong>Xuất Xứ:</strong>&nbsp;Pháp</p><p><strong>THÔNG TIN&nbsp; THƯƠNG HIỆU ROYAL CANIN - PHÁP</strong></p><p><a href=\"https://www.petcity.vn/brand/royal-canin\"><strong>Royal Canin</strong></a>&nbsp;là một người khổng lồ trên toàn cầu về dinh dưỡng sức khỏe vật nuôi có trụ sở tại Pháp. Trong một ngành công nghiệp mà tiếp tục để thích ứng với xu hướng phổ biến, nhiệm vụ của chúng tôi sẽ vẫn là liên tục mang lại, thông qua dinh dưỡng Y tế và chia sẻ kiến ​​thức, giải pháp dinh dưỡng chính xác nhất cho mèo và chó về nhu cầu sức khỏe, bằng cách xây dựng liên tục làm sâu sắc thêm kiến thức khoa học trong ngành công nghiệp phục vụ chó mèo.</p><br></p>', 120, 'Pháp', 2),
(7, '1576562834_SmartHeart-Adult-Power-Pack-(1kg).jpg', 'SmartHeart Adult Power Pack (1kg)', 'SmartHeart Adult Power Pack 1kg', 'SmartHeart – thức ăn chứa hàm lượng dinh dưỡng tốt nhất dành cho Cún cưng được sản xuất tại Thái Lan, với các dòng sản phẩm chuyên biệt với chức năng và chất dinh dưỡng được nghiên cứu đặc biệt để đáp ứng nhu cầu sức khỏe và thể chất của những giống chó khác nhau.', '<p><strong><a href=\"https://www.petcity.vn/smartheart-adult-power-pack-1kg.html\">Smartheart Adult Power Pack 1kg</a></strong><br></p><p><p><em>Dành cho chó trưởng thành&nbsp;</em></p><ul><li>Năng lượng cao (Để nuôi chó con)</li><li>Tăng cường cơ bắp và phát triển đúng cách</li><li>Canxi cao cho sức mạnh của cấu trúc xương và răng</li><li>Cung cấp giá trị dinh dưỡng hoàn toàn puppy cơ thể khỏe mạnh</li><li>Omega 6 giúp nuôi dưỡng sức khỏe và tỏa sáng của làn da.</li><li>Vitamin E và Selen Giúp tăng cường chức năng miễn dịch</li><li>Carbohydrate từ gạo Giúp dễ tiêu hóa Tăng hiệu quả của hệ tiêu hóa</li></ul><p><strong>Giới thiệu về&nbsp;<a href=\"https://www.petcity.vn/brand/smartheart\">Smartheart</a>&nbsp;:&nbsp;</strong></p><p>SmartHeart – thức ăn chứa hàm lượng dinh dưỡng tốt nhất dành cho Cún cưng được sản xuất tại Thái Lan, với các dòng sản phẩm chuyên biệt với chức năng và chất dinh dưỡng được nghiên cứu đặc biệt để đáp ứng nhu cầu sức khỏe và thể chất của những giống chó khác nhau.</p><br></p>', 30, 'Thai Lan', 2),
(8, '1576563072_Pedigree---Sốt-chó-lớn-vị-gà-,-gan-nướng-&-rau.jpg', 'Pedigree - Sốt chó lớn vị gà , gan nướng & rau', 'Pedigree sot cho lon vi ga gan nuong va rau', 'Thức Ăn Cho Chó Pedigree 80g có công thức với từng miếng thịt mềm trong nước sốt cho hương vị thơm ngon mà những chú chó cực kì yêu thích. Một chế độ dinh dưỡng đầy đủ và cân bằng bao gồm các vitamin, khoáng chất, canxi, protein, chất xơ và omega cho cơ thể các chú cún có một sức khỏe toàn diện, bảo đảm sự phát triển cả về thể chất lẫn ngoại hình.', '<p><h3>MÔ TẢ SẢN PHẨM</h3><p></p><div><div><div><p>Thức Ăn Cho Chó Pedigree 80g&nbsp;có công thức với từng miếng thịt mềm trong nước sốt cho hương vị thơm ngon mà những chú chó cực kì yêu thích. Một chế độ dinh dưỡng đầy đủ và cân bằng bao gồm các vitamin, khoáng chất, canxi, protein, chất xơ và omega cho cơ thể các chú cún có một sức khỏe toàn diện, bảo đảm sự phát triển cả về thể chất lẫn ngoại hình.</p><p><strong>Thông tin sản phẩm</strong>: 80g x 1 gói</p><p><strong>Thành phần</strong></p><p>Thịt gà, nước thịt, vitamin C, A, D, D3, B2, B12, B6, E và vitamin B1, khoáng chất như canxi, đồng sulfat, kẽm, kali clorua, sắt, nghệ, chiết xuất trà xanh và muối i-ốt.</p><p><strong>Công dụng</strong></p><p>Sốt vị&nbsp;gà, gan nướng&nbsp;và rau&nbsp; tích hợp nhiều chất dinh dưỡng, bảo đảm những chú cún của bạn sẽ xin xắn, khỏe mạnh với Omega &amp; Zinc cho da và lông mượt mà, ít rụng, Protein cho cơ bắp chắc khỏe, dẻo dai.</p><p>Ngoài ra, Vitamin &amp; khoáng chất cho cơ thể những chú cún luôn khỏe mạnh, linh hoạt. Chất xơ cho hệ thống tiêu hóa tốt, tăng cường khả năng hấp thu chất dinh dưỡng, góp phần vào sự phát triển đồng đều cho các chú cún.</p><p>Chất Calsium &amp; phospho cho xương và răng chắc khoẻ.</p><p><strong>Hướng dẫn sử dụng</strong></p><p>Bạn có thể cho cún ăn bằng cách dùng ăn kèm với cơm hay bánh mì</p><p>Sản phẩm phù hợp với nhiều loại cún có độ tuổi khác nhau</p><p>Hạn sử dụng in trên bao bì</p></div></div></div><br></p>', 12, 'Hoa Kỳ', 3),
(9, '1576563246_Thức-ăn-đóng-túi-Pedigree-Sốt-Gà-Puppy.jpg', 'Thức ăn đóng túi Pedigree Sốt Gà Puppy', 'thuc an dong tui Pedigree sot ga Puppy', 'là thức ăn dạng sốt đóng túi cho chó, thơm ngon bổ dưỡng, bao gồm độ béo và khoáng chất tối đa giúp nuôi dưỡng da lông chó, và giúp chó tiêu hóa tốt hơn vì bao gồm những chất dinh dưỡng dễ dàng hấp thụ. ', '<p><h2><a href=\"https://www.petcity.vn/thuc-an-dong-tui-pedigree-sot-ga-puppy/p761.html\"><strong>Thức ăn đóng túi Pedigree Sốt Gà Puppy</strong></a></h2><p>là thức ăn dạng sốt đóng túi cho chó, thơm ngon bổ dưỡng, bao gồm độ béo và khoáng chất tối đa giúp nuôi dưỡng da lông chó, và giúp chó tiêu hóa tốt hơn vì bao gồm những chất dinh dưỡng dễ dàng hấp thụ.&nbsp;</p><p><strong>Nguyên liệu</strong></p><p><a href=\"https://www.petcity.vn/brand/pedigree\"><em><strong>Thức ăn đóng túi Pedigree</strong></em></a>&nbsp;bao gồm : Thịt (các loại), trứng sấy khô, dầu thực vật, dầu cá, DHA, rau củ, nước, vitamin như vitamin C, E, A, D và B1, các loại khoáng chất như canxi, kẽm, đồng sulfate, kali.</p><p><strong>Khối lượng</strong>&nbsp;: 150gr</p><p><strong>Xuất xứ</strong>&nbsp;: Mỹ</p><p><strong>Thành phần dinh dưỡng</strong></p><p>Độ đạm (tối thiểu) ..................................... 8%</p><p>Độ béo (tối thiểu) ...................................... 3%</p><p>Chất xơ (tối đa) ......................................... 1 %</p><p>Độ ẩm (tối đa) ........................................... 82%</p><p>Linoleic Acid (tối thiểu) .............................. 0.7%</p><p>Vitamin E (tối thiểu) .................................. 25 IU/KG</p><p><strong>&nbsp;THÔNG TIN THƯƠNG HIỆU</strong></p><p><strong>Tại&nbsp;® Nhãn hiệuPedigree, tất cả mọi thứ chúng ta làm cho tình yêu của mình với con vật cưng, từ thức ăn dinh dưỡng cho chó, chúng tôi thực hiện tình yêu thông qua những con chó.&nbsp;Delicious thức ăn cho chó, chăm sóc chó và nhiều hơn thế nữa ...</strong></p><br></p>', 16, 'Hoa Kỳ', 3),
(10, '1576563481_Bánh-thưởng-hình-xương-AFP-Krazy-Crunch-Size-S.jpg', 'Bánh thưởng hình xương AFP Krazy Crunch Size S', 'Banh thuong hinh xuong  AFP Krazy Crunch Size S', 'Bánh thưởng hình xương AFP Krazy Crunch Size S', '<p><h2><a href=\"https://www.petcity.vn/Banh-thuong-hinh-xuong-AFP-Krazy-Crunch-4x3x1cm/p2955.html\">Bánh thưởng hình xương AFP Krazy Crunch 4x3x1cm</a></h2><p>Được thành lập từ năm 1997, với 20 năm kinh nghiệm, mục tiêu của AFP và Pawise là cung cấp các sản phẩm dành cho thú cưng với chất lượng tốt nhất, an toàn nhất với 95% sản phẩm đã được xuất khẩu sang thị trường Châu Âu và Châu Mỹ.</p><p>AFP và Pawise luôn cố gắng hết sức để thiết kế những sản phẩm nhằm đáp ứng các nhu cầu của vật nuôi cũng như phù hợp với phong cách của chủ nhân.</p><p>Với các nhà thiết kế đầy tính sáng tạo giàu kinh nghiệm, nhân viên chuyên nghiệp và đội ngũ quản lý có năng lực, AFP và Pawise đang khẳng định vị thế của mình trong lĩnh vực chăm sóc thú cưng, mong muốn mang đến những sản phẩm an toàn, đáng tin cậy cùng dịch vụ hiệu quả.</p><br></p>', 20, 'Phap', 4),
(11, '1576563774_Tellme---Đồ-gặm-da-bò-dạng-xoắn-trung-M.2.jpg', 'Tellme - Đồ gặm da bò dạng xoắn trung M.2', 'Tellme Do gam da bo dang xoan trung M2', 'Tellme - Đồ gặm da bò dạng xoắn trung M.2 (cho chó nhỏ, chó vừa)', '<p><h2><strong><a href=\"https://www.petcity.vn/tellme-do-gam-da-bo-dang-xoan-trung-m.2-cho-cho-nho-cho-vua.html\">Đồ gặm Da bò Tellme dạng xoắn trung M.2</a></strong>&nbsp; &nbsp; &nbsp;</h2><p><em>Quy cách :&nbsp;2 cái/túi – trọng lượng min 60 gram/túi</em></p><p><strong>TELLME® Da bò dạng xoắn trung cho chó nhỏ, chó vừa hoặc làm phần thưởng.</strong></p><p>√ 100% Da bò tự nhiên</p><p>√ Làm hoàn toàn thủ công, không hóa chất và sấy khô ở nhiệt độ thích hợp giúp giữ nguyên dưỡng chất.</p><p>√&nbsp;Cấu tạo xoắn giống như một chiếc bàn chải giúp nhẹ nhàng len sâu chải sạch từng kẽ răng và quanh viền nướu.</p><p>√ Collagen trong da bò giúp lông cún bóng mượt, khỏe mạnh, hạn chế rụng lông, làm chậm sự lão hóa của các tế bào cơ thể</p><p>√ Dễ tiêu hóa.</p><p>√ Món quà thơm ngon thỏa mãn tập tính gặm nhai của cún yêu.</p><p><strong>Giới thiệu về&nbsp;<a href=\"https://www.petcity.vn/brand/tellme-viet-nam\">Tellme</a></strong></p><p>- &nbsp;Được làm 100% Da bò tự nhiên</p><p>‌- Làm hoàn toàn thủ công, không hóa chất và sấy khô ở nhiệt độ thích hợp giúp giữ nguyên dưỡng chất.</p><p>-‌ Giúp răng chắc khỏe và sạch sẽ</p><p>‌- Collagen trong da bò giúp lông cún bóng mượt, khỏe mạnh, hạn chế rụng lông, làm chậm sự lão hóa của các tế bào cơ thể</p><br></p>', 100, 'Viet Nam', 6),
(12, '1576563894_Xương-phomai-Orgo-cho-dog-vị-sữa-90g.jpg', 'Xương phomai Orgo cho dog vị sữa 90g', 'Xuong phomai Orgo cho dog vi sua 90g', 'Xương phomai Orgo cho dog vị sữa 90g', '<p><h2>Xương phomai Orgo cho dog vị sữa 90g</h2><p></p><div><div><div><div><div><div><div><div><div><div><div><div><div><p><strong>ĐẶC ĐIỂM NỔI BẬT:</strong></p><p>- Làm sạch cao răng</p><p>- Tốt ngon miệng</p><p>- Pho mát cao cấp chọn lọc</p><p>- Bổ sung canxi rõ ràng</p><p>- Giúp xoa bóp, tăng cường và cải thiện tình trạng nướu</p></div></div></div></div></div></div></div></div></div></div></div></div></div><br></p>', 19, 'Viet Nam', 6);

